import React from "react";

const StudentList = (props) => {
	const students = props.students.map(
		({ firstName, city, company, email, grades, id, lastName, pic, skill }) => {
			console.log(
				firstName,
				city,
				company,
				email,
				grades,
				id,
				lastName,
				pic,
				skill
			);

			const makeAverage = (array) => {
				let total = 0;
				let avg;
				for (let i = 1; i < array.length; i++) {
					total += +array[i];
				}
				avg = total / 8;
				console.log(avg);
			};

			return (
				<div className="ui cards">
					<div className="card">
						<div className="ui divided items">
							<div className="item">
								<div
									style={{ border: "1px solid black" }}
									className="ui circular small image"
								>
									<img src={pic} />
								</div>
							</div>
							<div className="content">
								<h1 className="ui header">
									{firstName} {lastName}
								</h1>
								<div className="description">Email: {email}</div>
								<div className="description">Company: {company}</div>
								<div className="description">Skill: {skill}</div>
								<div className="description">Average:{makeAverage(grades)}</div>
							</div>
						</div>
					</div>
				</div>
			);
		}
	);
	return <div>{students}</div>;
};

export default StudentList;
