import React from "react";
import hatchways from "../hatchways";
import SearchBar from "./SearchBar";
import StudentList from "./StudentList";

class App extends React.Component {
	state = { students: [] };

	onSearchSubmit = async (term) => {
		const response = await hatchways.get("/search/photos", {
			params: { query: term },
		});
		this.setState({ images: response.data.students });
	};
	componentDidMount = async () => {
		const response = await hatchways.get("/assessment/students");
		this.setState({ students: response.data.students });

		// this.setState({ students: response });
	};
	render() {
		return (
			<div className="ui container">
				<SearchBar handleSubmit={this.onSearchSubmit} />
				<StudentList students={this.state.students} />
			</div>
		);
	}
}

export default App;
