import React from "react";
import "./StudentList.css";

const StudentList = (props) => {
	const students = props.students.map(
		({ firstName, city, company, email, grades, id, lastName, pic, skill }) => {
			console.log(
				firstName,
				city,
				company,
				email,
				grades,
				id,
				lastName,
				pic,
				skill
			);

			const makeAverage = (array) => {
				let total = 0;
				let avg;
				for (let i = 0; i < array.length; i++) {
					total += +array[i];
				}
				avg = total / array.length;
				return avg;
			};

			return (
				<div className="item">
					<img class="ui avatar image" src={pic}
							id={id}
							alt="profile" />
					<div class="content">
						<a class="header">{firstName} {lastName}</a>
						<div class="description">
						Email: {email}
						Email: {email}
						</div>
					</div>

					<div className="ui left floated">
						<img
							className="ui small image circular bordered"
							src={pic}
							id={id}
							alt="profile"
						/>
					</div>
					<div className="content">
						<h1 className=" header">
							{firstName} {lastName}
						</h1>

						<div className="description">Email: {email}</div>
						<div className="description">Email: {email}</div>
						<div className="description">Skill: {skill}</div>
						<div className="description">Average:{makeAverage(grades)}</div>
					</div>
				</div>
			);
		}
	);
	return <div className="ui relaxed divided list">{students}</div>;
};

export default StudentList;
