import React from "react";
import "./StudentList.css";

const StudentList = (props) => {
	const students = props.students.map(
		({ firstName, city, company, email, grades, id, lastName, pic, skill }) => {
			console.log(
				firstName,
				city,
				company,
				email,
				grades,
				id,
				lastName,
				pic,
				skill
			);

			const makeAverage = (array) => {
				let total = 0;
				let avg;
				for (let i = 0; i < array.length; i++) {
					total += +array[i];
				}
				avg = total / array.length;
				return avg;
			};

			return (
				<div className="item">
					<img
						class="ui small avatar image circular bordered"
						src={pic}
						id={id}
						alt="profile"
					/>
					<div class="content">
						<a class="header">
							{firstName} {lastName}
						</a>
						<div class="description">
						<div class="ui list">
  <div class="item">Apples</div>
  <div class="item">Pears</div>
  <div class="item">Oranges</div>
</div>
							Email: {email}
							Company: {company}
							Skill: {skill}
							Average:{makeAverage(grades)}
						</div>
					</div>
				</div>
			);
		}
	);
	return <div className="ui relaxed divided list">{students}</div>;
};

export default StudentList;
