import React from "react";
import hatchways from "../hatchways";
import SearchBar from "./SearchBar";
import StudentList from "./StudentList";

class App extends React.Component {
	state = { students: [] };

	handleCallback = (term) => {
		console.log(term);
		this.setState({ students: this.filterName(term) });
	};

