import React from "react";
import "./App.css";

const StudentList = (props) => {
	const students = props.students.map(({ firstName, city, company, email, grades, id, lastName, pic, skill }) => {
		const average = (array) => {
			let total = 0;
			let avg;
			for (let i = 0; i < array.length; i++) {
				total += +array[i];
			}
			avg = total / array.length;
			return avg;
		};

		return (
			<div className="item each" key={id}>
				<img className="ui avatar small image circular bordered" src={pic} alt="alien profiles" />
				<div className="content">
					<h1 className="header">
						{firstName} {lastName}
					</h1>
					<div className="ui list description">
						<div className="item">Email: {email}</div>
						<div className="item">Company: {company}</div>
						<div className="item">Skill: {skill}</div>
						<div className="item">Average: {average(grades)}%</div>
					</div>
				</div>
				<button className="ui right floated button"><i className/></button>
			</div>
		);
	});
	return <div className="ui relaxed divided list no-margin">{students}</div>;
};

export default StudentList;
