import StudentItem from "./StudentItem";

import AddTag from "./AddTag";
import Button from "./Button";
import "./App.css";

const StudentList = (props) => {
	const [visibility, setVisibility] = useState(false);
	const [tags, setTags] = useState([]);

	const iconToggle = () => {
		setVisibility(!visibility);
		showGrade(grades);
	};

	const displayTags = tags.map((tag) => {
		return (
			<button className="ui button" key={tag}>
				{tag}
			</button>
		);
	});
	const students = props.students.map((student, tagTerm) => {
		return (
			<StudentItem key={student.id} student={student} tagTerm={tagTerm}>
				<Addtag setTag={setTag} displayTags={displayTags} />
				<Button onClick={iconToggle} />
			</StudentItem>
		);
	});

	return <div className="ui relaxed divided list no-margin">{students}</div>;
};

export default StudentList;
