import React, { useState } from "react";
import "./App.css";

const StudentItem = (props) => {
	const [visibility, setVisibility] = useState(false);
	const { firstName, city, company, email, grades, id, lastName, pic, skill } = props.student;

	const iconToggle = () => {
		setVisibility(!visibility);
	};

	const average = (array) => {
		let total = 0;
		let avg;
		for (let i = 0; i < array.length; i++) {
			total += +array[i];
		}
		avg = total / array.length;
		return avg;
	};

	return (
		<div className="item each" key={id}>
			<img className="ui avatar small image circular bordered" src={pic} alt="alien profiles" />
			<div className="content">
				<h1 className="header">
					{firstName} {lastName}
				</h1>
				<div className="ui list description">
					<div className="item">Email: {email}</div>
					<div className="item">Company: {company}</div>
					<div className="item">Skill: {skill}</div>
					<div className="item">Average: {average(grades)}%</div>
				</div>
			</div>
			<button class="ui right floated icon button no-background no-margin" onClick={iconToggle}>
				{visibility ? <i class="plus icon"></i> : <i class="minus icon"></i>}
			</button>
		</div>
	);
};

export default StudentItem;
