import React from "react";
import hatchways from "../hatchways";
import SearchBar from "./SearchBar";
import StudentList from "./StudentList";

class App extends React.Component {
	state = { students: [] };

	handleCallback = (term) => {
		console.log(term);
		this.setState({ students: this.filterName(term) });
	};

	fetchStudents = async () => {
		const response = await hatchways.get("/assessment/students");
		this.setState({ students: response.data.students });
	};

	filterName = (term) => {
		this.fetchStudents();

		const filtered = [];
		this.state.students.forEach((student) => {
			if (student.firstName.toUpperCase().includes(term.toUpperCase())) {
				filtered.push(student);
			} else if (student.lastName.toUpperCase().includes(term.toUpperCase())) {
				filtered.push(student);
			}
		});
		console.log(filtered);
		return filtered;
	};

	componentDidMount() {
		this.fetchStudents();

		// this.setState({ students: response });
	}

	render() {
		return (
			<div className="ui container">
				<SearchBar handleCallback={this.handleCallback} />
				<StudentList students={this.state.students} />
			</div>
		);
	}
}

export default App;
