import React from "react";
import hatchways from "../hatchways";
import SearchBar from "./SearchBar";
import StudentList from "./StudentList";

class App extends React.Component {
	state = { students: [] };

	onSubmit = (term) => {
		const filtered = this.state.students;
		filtered.forEach((student) => {
			if (student.firstName.toUpperCase().includes(term.toUpperCase())) {
				console.log(student);
			} else if (student.lastName.toUpperCase().includes(term.toUpperCase())) {
				console.log(student);
			}
		});
		// this.setState({students: student})
	};

	componentDidMount = async () => {
		const response = await hatchways.get("/assessment/students");
		this.setState({ students: response.data.students });

		// this.setState({ students: response });
	};

	render() {
		return (
			<div className="ui container">
				<SearchBar
					// handleSubmit={this.onSubmit}
					students={this.state.students}
				/>
				<StudentList students={this.state.students} />
			</div>
		);
	}
}

export default App;
