import React from "react";

class SearchBar extends React.Component {
	state = { term: "", filtered: [] };

	onSubmit = (event) => {
		event.preventDefault();

		studthis.filterName(this.state.term);
		this.props.parentCallback(this.filterName);
	};

	filterName = (term) => {
		const filtered = this.props.students;
		filtered.forEach((student) => {
			if (student.firstName.toUpperCase().includes(term.toUpperCase())) {
				return student;
			} else if (student.lastName.toUpperCase().includes(term.toUpperCase())) {
				return student;
			}
		});
	};

	render() {
		return (
			<div className="ui segment">
				<form onSubmit={this.onSubmit} className="ui form">
					<div className="field">
						<label>Image Search</label>
						<input
							type="text"
							value={this.state.term}
							onChange={(e) => {
								this.setState({ term: e.target.value });
							}}
						/>
					</div>
				</form>
			</div>
		);
	}
}
export default SearchBar;
