import React from "react";
import hatchways from "../hatchways";
import SearchBar from "./SearchBar";
import StudentList from "./StudentList";

class App extends React.Component {
	state = { students: [] };

	handleCallback = (term) => {
		this.setState({ term: term });
	};

	fetchStudents = async () => {
		const response = await hatchways.get("/assessment/students");
		this.setState({ students: response.data.students });
	};

	filterStudents() {


		if (this.state.term) {
			const filtered = [];
			this.state.students.forEach((student) => {
				if (
					student.firstName
						.toUpperCase()
						.includes(this.state.term.toUpperCase()) ||
					student.lastName.toUpperCase().includes(this.state.term.toUpperCase())
				) {
					filtered.push(student);
				}
			});
			return filtered;
		} else {
			return this.state.students;
		}
	}

	componentDidMount() {
		this.fetchStudents();

		// this.setState({ students: response });
	}

	render() {
		return (
			<div className="ui container">
				<SearchBar handleCallback={this.handleCallback} />
				<StudentList students={this.filterStudents()} />
			</div>
		);
	}
}

export default App;
