import React from "react";
import "./StudentList.css";

const StudentList = (props) => {
	const students = props.students.map(
		({ firstName, city, company, email, grades, id, lastName, pic, skill }) => {
			console.log(
				firstName,
				city,
				company,
				email,
				grades,
				id,
				lastName,
				pic,
				skill
			);

			const average = (array) => {
				let total = 0;
				let avg;
				for (let i = 0; i < array.length; i++) {
					total += +array[i];
				}
				avg = total / array.length;
				return avg;
			};

			return (
				<div class="item">
					<img
						class="ui avatar small image circular bordered"
						src={pic}
						alt="pica dura"
					/>
					<div class="content">
						<h1 class="header">
							{firstName} {lastName}
						</h1>
						<div class="ui list description">
							<div class="item">Email{}</div>
							<div class="item"></div>
							<div class="item"></div>
						</div>
					</div>
				</div>
			);
		}
	);
	return <div className="ui relaxed divided list">{students}</div>;
};

export default StudentList;
