import React, { useState } from "react";
import "./App.css";
import Button from "./Button";

const StudentItem = (props) => {
	const [visibility, setVisibility] = useState(false);
	const [iconClass, setIconClass] = useState("plus icon");
	const { firstName, company, email, grades, id, lastName, pic, skill } = props.student;

	const openGrade = () => {
		console.log("hello");
	};

	const iconToggle = () => {
		setVisibility(!visibility);
		setIconClass(visibility ? "plus icon" : "minus icon");
		openGrade();
	};

	const average = (array) => {
		let total = 0;
		let avg;
		for (let i = 0; i < array.length; i++) {
			total += +array[i];
		}
		avg = total / array.length;
		return avg;
	};

	return (
		<div className="item each" key={id}>
			<img className="ui avatar small image circular bordered" src={pic} alt="alien profiles" />
			<div className="content">
				<h1 className="header">
					{firstName} {lastName}
				</h1>
				<div className="ui list description">
					<div className="item">Email: {email}</div>
					<div className="item">Company: {company}</div>
					<div className="item">Skill: {skill}</div>
					<div className="item">Average: {average(grades)}%</div>
				</div>
			</div>
			{/* <button className="ui right floated icon button no-background no-margin" onClick={iconToggle}>
				<i className={iconClass}></i>
			</button> */}


		</div>
	);
};

export default StudentItem;
